# --- TAB 1: Single Country ---
with tab1:
    st.header("Analýza trendů HDP per capita")
    c_single = st.selectbox("Vyberte zemi:", selected_countries)
    
    df_s = df_filtered[df_filtered['Country'] == c_single].sort_values('Year')
    
    if len(df_s) > 2:
        X_years = df_s['Year'].values
        y = df_s['GDP'].values
        
        # [修改點 1] 時間歸零校正: t 從 0, 1, 2... 開始
        t = X_years - X_years.min() 
        t_reshaped = t.reshape(-1, 1)

        # 1. Linear (線性回歸)
        mod_lin = LinearRegression().fit(t_reshaped, y)
        y_lin = mod_lin.predict(t_reshaped)
        r2_lin = r2_score(y, y_lin)
        
        # [修改點 2] 格式化方程式字串，處理正負號，使用 t 作為變數
        sign_lin = "+" if mod_lin.intercept_ >= 0 else "-"
        eq_lin = f"y = {mod_lin.coef_[0]:.2f}t {sign_lin} {abs(mod_lin.intercept_):.2f}"
        
        # 2. Quadratic (二次回歸)
        poly = PolynomialFeatures(2)
        X_poly = poly.fit_transform(t_reshaped)
        mod_quad = LinearRegression().fit(X_poly, y)
        y_quad = mod_quad.predict(X_poly)
        r2_quad = r2_score(y, y_quad)
        
        # 格式化二次方程式字串
        sign_quad_1 = "+" if mod_quad.coef_[1] >= 0 else "-"
        sign_quad_0 = "+" if mod_quad.intercept_ >= 0 else "-"
        eq_quad = f"y = {mod_quad.coef_[2]:.2f}t\u00b2 {sign_quad_1} {abs(mod_quad.coef_[1]):.2f}t {sign_quad_0} {abs(mod_quad.intercept_):.2f}"
        
        # 3. Exponential (S-S Model)
        
        # --- 針對台灣的特殊處理：使用 ln(bazických indexů) 進行迴歸 ---
        if c_single == 'Taiwan' and 'ln_Base_Index' in df_s.columns:
            # 迴歸因變數：ln(GDP_t / GDP_1971)
            y_log = df_s['ln_Base_Index'].values
            
            # 迴歸自變數：時間 t，從 1971 年開始，t=0
            # 由於 t 已經在前面計算為 t = X_years - X_years.min()，且 X_years.min() = 1971，所以 t 已經是從 0 開始
            
            mod_exp = LinearRegression().fit(t_reshaped, y_log)
            
            # 提取係數
            slope_exp = mod_exp.coef_[0]      # 成長率 g
            intercept_exp = mod_exp.intercept_ # ln(A0)
            
            # 初始值 A0 應該是 ln(GDP_1971 / GDP_1971) = ln(1) = 0
            # 但迴歸得到的截距是 ln(A0)，所以 A0 = exp(intercept_exp)
            # 為了匹配 Excel 範例，我們將 A0 設為 1971 年的 GDP 值
            A_0 = df_s[df_s['Year'] == df_s['Year'].min()]['GDP'].iloc[0]
            
            # 重新計算 y_exp 和 R2
            # 預測 ln(GDP_t / GDP_1971)
            y_log_pred = mod_exp.predict(t_reshaped)
            
            # 預測 GDP_t = GDP_1971 * exp(y_log_pred)
            gdp_1971 = df_s[df_s['Year'] == df_s['Year'].min()]['GDP'].iloc[0]
            y_exp = gdp_1971 * np.exp(y_log_pred)
            
            r2_exp = r2_score(y, y_exp)
            
            # 寫法 A: 對數形式 (ln(y/y0) = gt + ln(A))
            sign_exp_log = "+" if intercept_exp >= 0 else "-"
            eq_exp_log = f"ln(y/y_{df_s['Year'].min()}) = {slope_exp:.4f}t {sign_exp_log} {abs(intercept_exp):.4f}"
            
        # --- 其他國家或台灣使用標準 ln(GDP) 迴歸 ---
        else:
            y_log = np.log(y)
            mod_exp = LinearRegression().fit(t_reshaped, y_log)
            y_exp = np.exp(mod_exp.predict(t_reshaped))
            r2_exp = r2_score(y, y_exp)
            slope_exp = mod_exp.coef_[0]
            intercept_exp = mod_exp.intercept_
            A_0 = np.exp(intercept_exp)
            
            # 寫法 A: 對數形式 (ln(y) = gt + ln(A))
            sign_exp_log = "+" if intercept_exp >= 0 else "-"
            eq_exp_log = f"ln(y) = {slope_exp:.4f}t {sign_exp_log} {abs(intercept_exp):.4f}"
            
        # 寫法 B: 指數形式 (S-S 理論形式 y = A * e^gt)
        # 為了與 Excel 範例的格式 y = 2068.3e0.0577x 一致，移除 \cdot 和 {}
        eq_exp_real = f"y = {A_0:.4g}e^{{{slope_exp:.4f}t}}" # 使用 .4g 確保數字精度，並使用 t 作為變數
        
        # Plot
        fig = go.Figure()
        fig.add_trace(go.Scatter(x=df_s['Year'], y=y, mode='markers', name='HDP per capita Data'))
        fig.add_trace(go.Scatter(x=df_s['Year'], y=y_lin, mode='lines', name=f'Lineární HDP per capita (R\u00b2={r2_lin:.4f})', line=dict(dash='dash')))
        fig.add_trace(go.Scatter(x=df_s['Year'], y=y_quad, mode='lines', name=f'Kvadratická HDP per capita (R\u00b2={r2_quad:.4f})'))
        fig.add_trace(go.Scatter(x=df_s['Year'], y=y_exp, mode='lines', name=f'S-S Model HDP per capita (R\u00b2={r2_exp:.4f})'))
        
        st.plotly_chart(fig, use_container_width=True)
        
        # st.markdown("#### Rovnice modelů (Model Equations / 模型方程式)") # 移除標題
        
        # 1. Lineární (Linear)
        st.write(f"**1. Lineární (Linear):** ${eq_lin}$")
        st.caption("Vysvětlení: Předpokládá se, že roční nárůst HDP per capita je konstantní (přímka).")
        
        # 2. Kvadratická (Quadratic)
        st.write(f"**2. Kvadratická (Quadratic):** ${eq_quad}$")
        st.caption("Vysvětlení: Předpokládá se, že rychlost růstu HDP per capita se zrychluje nebo zpomaluje (parabola).")
        
        # 3. Exponenciální (S-S Model HDP per capita)
        st.write(f"**3. Exponenciální (S-S Model HDP per capita):**")
        st.write(f"- Logaritmická forma: ${eq_exp_log}$")
        st.write(f"- Exponenciální forma: ${eq_exp_real}$")
        st.caption(f"Vysvětlení: Model Solow-Swan, předpokládá roční složený růst HDP per capita o pevné procento ($g={slope_exp:.2%}$).")
        
    else:
        st.write("Nedostatek dat.")

# --- TAB 2: Convergence ---
def get_trendline_analysis(df, x_col, y_col, title_prefix):
    """計算線性趨勢線, 方程式和 R\u00b2, 並生成圖表和文字分析。"""
    if len(df) < 2:
        return None, f"Nedostatek dat pro {title_prefix} analýzu."

    X = df[x_col].values.reshape(-1, 1)
    y = df[y_col].values
    
    # 使用年份作為 X 軸，但為了迴歸計算，我們將其標準化為從 0 開始
    t = X - X.min()
    
    mod = LinearRegression().fit(t, y)
    y_pred = mod.predict(t)
    
    slope = mod.coef_[0]
    intercept = mod.intercept_
    r2 = r2_score(y, y_pred)
    
    # 趨勢線方程式: y = slope * (Year - min_Year) + intercept
    # 為了顯示更直觀的方程式，我們使用 Year 作為變數
    # y = slope * Year + (intercept - slope * min_Year)
    adjusted_intercept = intercept - slope * X.min()
    eq = f"y = {slope:.4f}x + {adjusted_intercept:.4f}"
    
    # 繪圖
    fig = px.line(df, x=x_col, y=y_col, title=f"{title_prefix} Konvergence", markers=True)
    
    # 新增趨勢線
    x_range = np.linspace(df[x_col].min(), df[x_col].max(), 100).reshape(-1, 1)
    t_range = x_range - X.min()
    y_range = mod.predict(t_range)
    
    fig.add_trace(go.Scatter(x=x_range.flatten(), y=y_range, mode='lines', name=f'Trendline (R\u00b2={r2:.4f})', line=dict(color='red', dash='dash')))
    
    # 新增方程式和 R\u00b2
    fig.add_annotation(
        x=0.95, y=0.95, xref="paper", yref="paper",
        text=f"<b>{eq}<br>R\u00b2={r2:.4f}</b>",
        showarrow=False, font=dict(size=14, color="red"), bgcolor="white",
        xanchor='right', yanchor='top'
    )
    
    # Analýza textu (Text Analysis)
    if slope < 0:
        analysis = f"Sklon trendové čáry je **{slope:.4f}** (záporný). To naznačuje **konvergenci** ({title_prefix} se zmenšuje), což znamená, že ekonomické rozdíly mezi vybranými zeměmi se zmenšují."
    elif slope > 0:
        analysis = f"Sklon trendové čáry je **{slope:.4f}** (kladný). To naznačuje **divergenci** ({title_prefix} se zvětšuje), což znamená, že ekonomické rozdíly mezi vybranými zeměmi se zvětšují."
    else:
        analysis = f"Sklon trendové čáry je **{slope:.4f}** (blízký nule). To naznačuje **stabilitu** ({title_prefix} se nemění), což znamená, že ekonomické rozdíly mezi vybranými zeměmi zůstávají stabilní."
        
    return fig, analysis

with tab2:
    st.header("Analýza konvergence")
    
    # Beta Convergence
    st.subheader("1. Beta Konvergence")
    
    beta_list = []
    start_y_val = year_range[0]
    end_y_val = year_range[1]
    
    for c in selected_countries:
        c_df = df[df['Country'] == c]
        row_start = c_df[c_df['Year'] == start_y_val]
        row_end = c_df[c_df['Year'] == end_y_val]
        
        if not row_start.empty and not row_end.empty:
            y0 = row_start.iloc[0]['GDP']
            yT = row_end.iloc[0]['GDP']
            T = end_y_val - start_y_val
            
            if T > 0 and y0 > 0 and yT > 0:
                g = (np.log(yT) - np.log(y0)) / T
                beta_list.append({'Country': c, 'ln_Init': np.log(y0), 'Growth': g})
    
    if len(beta_list) > 1:
        df_b = pd.DataFrame(beta_list)
        X_b = df_b['ln_Init'].values.reshape(-1, 1)
        y_b = df_b['Growth'].values
        
        mod_b = LinearRegression().fit(X_b, y_b)
        slope = mod_b.coef_[0]
        intercept = mod_b.intercept_
        r2_b = r2_score(y_b, mod_b.predict(X_b))
        
        eq_b = f"y = {slope:.4f}x + {intercept:.4f}"
        
        fig_b = px.scatter(df_b, x='ln_Init', y='Growth', text='Country',
                           title=f"Beta Konvergence (Slope: {slope:.5f})",
                           labels={'ln_Init': 'ln(GDP start)', 'Growth': 'Avg Growth'})
        
        # Add Trendline
        x_range = np.linspace(df_b['ln_Init'].min(), df_b['ln_Init'].max(), 100).reshape(-1, 1)
        y_range = mod_b.predict(x_range)
        fig_b.add_trace(go.Scatter(x=x_range.flatten(), y=y_range, mode='lines', name='Regrese', line=dict(color='red')))
        
        # Add Equation
        fig_b.add_annotation(
            x=0.95, y=0.95, xref="paper", yref="paper",
            text=f"<b>{eq_b}<br>R\u00b2={r2_b:.4f}</b>",
            showarrow=False, font=dict(size=14, color="red"), bgcolor="white"
        )
        
        st.plotly_chart(fig_b, use_container_width=True)
        
